import Foundation

let batchawana = Park(name: "batchawana",
                      about: "\u{2022} The drive along Highway 17 near Batchawana Bay offers outstanding views of Lake Superior. And Batchawana Bay Provincial Park is a perfect spot to stop and immerse yourself in this stunning scenery.\n\u{2022} Enjoy a beautiful beach with warm water on scenic Lake Superior, an exceptional picnic site and a good location with easy access for a short paddle.\n\u{2022} A plaque erected by the Ontario Motor League highlights Batchawana Bay (at Chippewa Falls) as the mid-point in the longest national highway in the world - the Trans-Canada Highway.",
                      general: "\u{2022} Phone: (705) 882-2209\n\u{2022} Size: 169.03 ha\n\u{2022} Year established: 1973\n\u{2022} Park Classification: Recreational\n\u{2022} Address:\n\t12729 Hwy 17N P.0. Box 61\n\tBatchawana Bay, ON\n\tP0S 1A0",
                      camping: [0: (heading: "This park is open from mid-May to October for day-use only.", detail: "This park is open from mid-May to October for day-use only.")],
                      activities: [0: (heading: "\(aBtg)", detail: "Boats must be carried into the water or driven to a launch east of the park at the mouth of the Batchawana River. Sudden squalls occur frequently and can be hazardous."), 1: (heading: "\(aFsh)", detail: "Rainbow trout, Northern Pike, Salmon, Lake Trout, and Walleye are all possible catches."), 2: (heading: "\(aSwn)", detail: "Batchawana Bay Provincial Park features a sandy beach that is the perfect setting for families. The water is shallow and protected and the warmest on the Lake Superior shoreline.")],
                      facilities: [0: (heading: "\(fDU)", detail: "A beach front area with picnic tables and vault toilets are available."), 1: (heading: "\(fPSh)", detail: "There is one picnic shelter in the park that is available on a first-come first-served basis.")])
