import Foundation

let north = Park(name: "north beach",
                 about: "\u{2022} Over one kilometre ribbon of sand shelters tiny North Beach from Lake Ontario\n\u{2022} The sandy beach is a great place to picnic on a summer day\n\u{2022} Swim or boat in the sheltered waters of the bay or the more open Lake Ontario\n\u{2022} A quieter park than its more famous neighbour - Sandbanks",
                 general: "\u{2022} Phone: 613-393-3319 (September to June) 613-399-2030 (3rd week of June to Labour Day)\n\u{2022} Size: 94.00 ha\n\u{2022} Year established: 1970\n\u{2022} Park Classification: Recreational \n\u{2022} Address:\n\t440 North Beach Rd\n\tConsecon\n\tK0K 2T0",
                 camping: [0: (heading: "North Beach is a day-use only park. There are no overnight camping facilities.", detail: "North Beach is a day-use only park. There are no overnight camping facilities.")],
                 activities: [0: (heading: "\(aBrd)", detail: "Visitors to North Beach may see hawks, Saw-whet Owls, and Turkey Vultures."), 1: (heading: "\(aBtg)", detail: "Launch your motorboat, canoe or sailboat from the park’s bay-side boat ramp."), 3: (heading: "\(aFsh)", detail: "Anglers visiting North Beach could catch Largemouth Bass, Northern Pike, perch and sunfish."), 4: (heading: "\(aSwm)", detail: "North Beach offers 1,200 metres of sandy beach facing Lake Ontario and another 800 metres of sand beach on North Bay. Waters drop off gradually on the lake side and steeply on the bay side. \n\nPlease be aware of undertow conditions on Lake Ontario during windy days.")],
                 facilities: [0: (heading: "\(fBL)", detail: "A boat launch is located on the north side of the park. It is suitable for small craft only. Personal floatation devices are available for loan at the park (refundable deposit is required)."), 1: (heading: "\(fDU)", detail: "North Beach is a day-use only park and is therefore the perfect place to enjoy a picnic.  Picnic tables and toilet facilities are conveniently located along the beach.  There is plenty of parking for visitors."), 2: (heading: "\(fPSt)", detail: "North Beach has a small canteen where park souvenirs and traditional fast food and beverages can be purchased. It is located along the park’s main road.")])
