import Foundation

let ouimet = Park(name: "ouimet canyon",
                  about: "\u{2022} Panoramic views of a 150 metre wide gorge and sheer cliffs that drop 100 metres straight down to the canyon floor\n\u{2022} A trail and boardwalk connects two lookout platforms with spectacular views of the canyon\n\u{2022} Arctic plants, usually found 1,000 kilometres north, survive in the unique environment at the bottom of the canyon\n\u{2022} Operated in partnership with the Friends of Ouimet Canyon\n\u{2022} Day-use park only park",
                  general: "\u{2022} Phone: (807) 977-2526\n\u{2022} Size: 777.02 ha\n\u{2022} Year established: 1972\n\u{2022} Park Classification: Nature Reserve \n\u{2022} Address:\n\tOuimet Canyon Provincial Park c/o Sleeping Giant Provincial Park R.R. #1\n\tPass Lake\n\tP0T 2M0",
                  camping: [0: (heading: "Ouimet Canyon is a day-use only park. There are no overnight camping facilities.", detail: "Ouimet Canyon is a day-use only park. There are no overnight camping facilities.")],
                  activities: [0: (heading: "\(aBrd)", detail: "Ouimet Canyon provides great opportunities for bird watching, as sections of the raised boardwalk trail get you closer to the forest canopy to see and hear neotropical migrants (birds that breed in North America and spend winters in Central and South America), as well as year round residents of the boreal forest.  You many also get a glimpse of the threatened Peregrine Falcon, which occasionally nests on the cliffs of the canyon."), 1: (heading: "\(aHik)", detail: "Ouimet Canyon features a one kilometre loop trail to two viewing platforms along the canyon’s rim. Please stay on the trail and view the canyon only from the designated lookout points. One route/direction along this trail allows for barrier-free access to the lookouts.")],
                  facilities: [0: (heading: "\(fBF)", detail: "The park’s vault toilets and one route to the canyon rim are barrier-free."), 1: (heading: "\(fDU)", detail: "Ouimet Canyon is a day-use park with a picnic area located near the main parking lot and trailhead."), 2: (heading: "\(fPSt)", detail: "A small store located at the trailhead is operated by the Friends of Ouimet Canyon.")])
